 // \u7559\u51fa\u7a7a\u95f4\u7ed9 DOM \u5143\u7d20\n});\n\nconst layer = new Konva.Layer();\nstage.add(layer);\n\n// \u8ddf\u8e2a\u62d6\u52a8\u5143\u7d20\u7684 URL\nlet itemURL = '';\ndocument\n  .getElementById('drag-items')\n  .addEventListener('dragstart', function (e) {\n    itemURL = e.target.src;\n  });\n\n// \u5904\u7406\u5bb9\u5668\u4e0a\u7684 dragover\nconst container = stage.container();\ncontainer.addEventListener('dragover', function (e) {\n  e.preventDefault(); // \u91cd\u8981 - \u5fc5\u987b\u963b\u6b62\u9ed8\u8ba4\u884c\u4e3a\n});\n\n// \u5904\u7406\u5bb9\u5668\u4e0a\u7684 drop\ncontainer.addEventListener('drop', function (e) {\n  e.preventDefault();\n  \n  // \u624b\u52a8\u6ce8\u518c\u6307\u9488\u4f4d\u7f6e\uff0c\u56e0\u4e3a\u8fd9\u662f\u4e00\u4e2a DOM \u4e8b\u4ef6\n  stage.setPointersPositions(e);\n  \n  // \u52a0\u8f7d\u56fe\u50cf\u5e76\u5c06\u5176\u6dfb\u52a0\u5230\u56fe\u5c42\n  Konva.Image.fromURL(itemURL, function (image) {\n    // \u6839\u636e\u56fe\u50cf\u5c3a\u5bf8\u8ba1\u7b97\u5408\u9002\u7684\u5927\u5c0f\n    const img = image.image();\n    const maxDimension = 100;\n    let width = img.width;\n    let height = img.height;\n    \n    if (width > height) {\n      height = (height / width) * maxDimension;\n      width = maxDimension;\n    } else {\n      width = (width / height) * maxDimension;\n      height = maxDimension;\n    }\n    \n    image.size({\n      width: width,\n      height: height\n    });\n    \n    layer.add(image);\n    image.position(stage.getPointerPosition());\n    image.draggable(true);\n  });\n});\n"})})}),(0,r.jsx)(o.A,{value:"React",children:(0,r.jsx)(e.pre,{children:(0,r.jsx)(e.code,{className:"language-js",metastring:"live react",live:!0,children:"import { useState, useRef, useEffect } from 'react';\nimport { Stage, Layer, Image } from 'react-konva';\nimport { useImage } from 'react-konva-utils';\n\nconst DragItem = ({ src, onDragStart }) => {\n  return (\n    <img \n      src={src} \n      draggable={true}\n      style={{ height: '100px', margin: '5px' }}\n      onDragStart={() => onDragStart(src)} \n    />\n  );\n};\n\nconst App = () => {\n  const [images, setImages] = useState([]);\n  const [dragImageSrc, setDragImageSrc] = useState('');\n  const stageRef = useRef(null);\n  \n  const handleDragStart = (src) => {\n    setDragImageSrc(src);\n  };\n  \n  const handleDragOver = (e) => {\n    e.preventDefault(); // \u963b\u6b62\u9ed8\u8ba4\u884c\u4e3a\n  };\n  \n  const handleDrop = (e) => {\n    e.preventDefault();\n    \n    if (!dragImageSrc || !stageRef.current) return;\n    \n    // \u83b7\u53d6\u821e\u53f0\u548c\u6307\u9488\u4f4d\u7f6e\n    const stage = stageRef.current;\n    \n    // \u624b\u52a8\u6ce8\u518c\u6307\u9488\u4f4d\u7f6e\uff0c\u56e0\u4e3a\u8fd9\u662f\u4e00\u4e2a DOM \u4e8b\u4ef6\n    stage.setPointersPositions(e);\n    const position = stage.getPointerPosition();\n    \n    // \u5c06\u65b0\u56fe\u50cf\u6dfb\u52a0\u5230\u5217\u8868\u4e2d\n    setImages([\n      ...images,\n      {\n        src: dragImageSrc,\n        x: position.x,\n        y: position.y,\n        id: Date.now().toString()\n      }\n    ]);\n  };\n  \n  return (\n    <div>\n      <p>\u5c06\u5c24\u8fbe\u62d6\u653e\u5230\u7070\u8272\u533a\u57df\u3002</p>\n      <div style={{ marginBottom: '10px' }}>\n        <DragItem \n          src=\"https://konvajs.org/assets/yoda.jpg\" \n          onDragStart={handleDragStart} \n        />\n        <DragItem \n          src=\"https://konvajs.org/assets/darth-vader.jpg\" \n          onDragStart={handleDragStart} \n        />\n      </div>\n      \n      <div \n        onDragOver={handleDragOver}\n        onDrop={handleDrop}\n        style={{ backgroundColor: 'rgba(0, 0, 0, 0.1)' }}\n      >\n        <Stage\n          width={window.innerWidth}\n          height={window.innerHeight - 150}\n          ref={stageRef}\n        >\n          <Layer>\n            {images.map((img) => (\n              <KonvaImage\n                key={img.id}\n                src={img.src}\n                x={img.x}\n                y={img.y}\n                draggable\n              />\n            ))}\n          </Layer>\n        </Stage>\n      </div>\n    </div>\n  );\n};\n\n// \u5355\u72ec\u7684\u7ec4\u4ef6\uff0c\u7528\u4e8e Konva \u56fe\u50cf\u7684\u6b63\u786e\u52a0\u8f7d\nconst KonvaImage = ({ src, x, y, draggable }) => {\n  const [image] = useImage(src);\n  \n  if (!image) return null;\n  \n  // \u8ba1\u7b97\u5408\u9002\u7684\u5927\u5c0f\n  const maxDimension = 100;\n  let width = image.width;\n  let height = image.height;\n  \n  if (width > height) {\n    height = (height / width) * maxDimension;\n    width = maxDimension;\n  } else {\n    width = (width / height) * maxDimension;\n    height = maxDimension;\n  }\n  \n  return (\n    <Image\n      image={image}\n      x={x}\n      y={y}\n      width={width}\n      height={height}\n      draggable={draggable}\n    />\n  );\n};\n\nexport default App;\n"})})}),(0,r.jsx)(o.A,{value:"Vue",children:(0,r.jsx)(e.pre,{children:(0,r.jsx)(e.code,{className:"language-js",metastring:"live vue",live:!0,children:'<template>\n  <div>\n    <p>\u5c06\u5c24\u8fbe\u62d6\u653e\u5230\u7070\u8272\u533a\u57df\u3002</p>\n    <div style="margin-bottom: 10px;">\n      <img \n        src="https://konvajs.org/assets/yoda.jpg" \n        draggable="true" \n        style="height: 100px; margin: 5px;"\n        @dragstart="handleDragStart(\'https://konvajs.org/assets/yoda.jpg\')" \n      />\n      <img \n        src="https://konvajs.org/assets/darth-vader.jpg" \n        draggable="true" \n        style="height: 100px; margin: 5px;"\n        @dragstart="handleDragStart(\'https://konvajs.org/assets/darth-vader.jpg\')" \n      />\n    </div>\n    \n    <div \n      @dragover.prevent\n      @drop="handleDrop"\n      style="background-color: rgba(0, 0, 0, 0.1);"\n    >\n      <v-stage \n        ref="stageRef"\n        :config="stageConfig"\n      >\n        <v-layer>\n          <v-image\n            v-for="img in images"\n   